import React, {useState} from 'react'

export default function TextForm(props) {
  const handleUpClick = ()=>{
    console.log("UpperCase Was Clicked" + text)
    let newText = text.toUpperCase();
    setText("You have clicked on handleUpClicked")
  }
  const handleOnChange = (event)=>{
    console.log("On Change")
    setText(event.target.value)
  }
  const[text,setText] = useState('Enter text here');
  // setText("New Text");
  return (  
<div>
  <h1>{props.heading}</h1>
  <div className="mb-3">
  <textarea className="form-control" value ={text} onChange ={handleOnChange} id="myBox" rows="9"></textarea>
  </div>
  <button className="btn-primary" onClick = {handleUpClick}>Convert to UpperCase</button>
</div>

   
  )
}
