import React, {useState} from 'react'

export default function TextForm(props) {
  const handleUpClick = ()=>{
    // console.log("UpperCase Was Clicked" + text)
    let newText = text.toUpperCase();
    setText(newText)
  }
  
  const handleLowClick = ()=>{
    let newText = text.toLowerCase();
    setText(newText)
  }
  const handleClearClick = ()=>{
    let newText = " ";
    setText(newText)
  }

  const handleOnChange = (event)=>{
    // console.log("On Change")
    setText(event.target.value)
  }
  const[text,setText] = useState('Enter text here');
  // setText("New Text");
  return ( 
<>
<div className="container">
  <h1>{props.heading}</h1>
  <div className="mb-3">
  <textarea className="form-control" value ={text} onChange ={handleOnChange} id="myBox" rows="9"></textarea>
  </div>
  <button className="btn btn-outline-success mx-2" onClick = {handleUpClick}>Convert to UpperCase</button>
  <button className="btn btn-outline-success mx-2" onClick = {handleLowClick}>Convert to LowerCase</button>
  <button className="btn btn-outline-success mx-2" onClick = {handleClearClick}>Text Clear</button>
</div>
<div className="container my-3">
  <h2>your text summary</h2>
  <p>{text.split(" ").length} Words And {text.length} charecters</p>
  <p>{0.008 * text.split(" ").length} Minutes read</p>
  <h2>Preview</h2>
  <p>{text}</p>
</div>
</>
  )
}
